
public class CastingEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//(타입) 캐스팅 연산자 -> 형변환
		//변수 또는 상수의 타입을 다른 타입으로 변환하는ㄴ 것
		
		//기본표현식
		//(타입)피연산자
		
		double d = 85.4;
		int score = (int)d;
		
		System.out.println("score = " + score);
		System.out.println("d = " + d);
	}

}
