
public class OperEx12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//비교 연산자
		// >, <, <=, >=, ==, !=
		//int num = 0;
		//크다, 작다, 초과, 미만
		System.out.println("2 >1 결과는: " + (2 > 1)); //true
		//크거나 같다, 작거나 같다, 이상, 이하
		System.out.println(2 <= 1);
		//같다
		System.out.println(2 == 1);
		//다르다
		System.out.println(2 != 1);
	}

}
