
public class OperEx13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//논리연산자
		// | : or 연산자, & : and 연산자
		int n = 0;
		
		n = 10;
		
		System.out.println(n % 2 == 0);
		
		System.out.println(n % 3 == 1);
		
		System.out.println(n % 2 == 0 || n % 3 == 0);
		
		System.out.println(n % 2 == 0 && n % 3 == 0);
	}

}
